""" UDF cz_sas_br_ind_av_individueel """

from pyspark.sql.functions import udf
from pyspark.sql.types import IntegerType


@udf(IntegerType())
def cz_sas_br_ind_av_individueel(
    product_code, soort_verzekerden_code, contractvorm_code
):
    condition_1 = product_code not in ["1147", "1192", "1343"]
    condition_2 = (
        soort_verzekerden_code.rstrip()[:2] in ["AV", "CO"] or product_code == "2844"
    ) and (soort_verzekerden_code != "COMBIBU" or product_code != "1091")
    condition_3 = soort_verzekerden_code not in ["HVV", "COMBIV"]
    condition_4 = soort_verzekerden_code not in ["HVBU", "COMBIBU"]
    condition_5 = contractvorm_code == "IND"

    return (
        1
        if (condition_1 and condition_2 and condition_3 and condition_4 and condition_5)
        else 0
    )
